import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import validation from './LoginValidation';
 function Signup() {
    const [values,setValues]=useState({
        name:"",

        email:"",
        password:""
    })
    const [errors,setErrors]=useState({

    })
    const handleSubmit=(event)=>{
        event.preventDefault();
        setErrors(validation(values));
    }
    const handleInput=(event)=>{
        setValues(prev=>({...prev,[event.target.name]:event.target.value}))
    }
  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100 '>
        <div className='bg-white p-3 rounded w-25'>
            <form className="mb-3" onSubmit={handleSubmit}>
            <div>
                    <label htmlFor='name'><strong>Name</strong></label>
                    <input type='email' className='form-control rounded-0' name="email" placeholder='Enter Email' onChange={handleInput}/>
                    {errors.email && <span className='text-danger'>{errors.email}</span>}

                </div>
                <div>
                    <label htmlFor='email'><strong>Email</strong></label>
                    <input type='email' className='form-control rounded-0' name="email" placeholder='Enter Email' onChange={handleInput}/>
                    {errors.email && <span className='text-danger'>{errors.email}</span>}

                </div>
                <div>
                    <label htmlFor='password'><strong>Password</strong></label>
                    <input type='password' onChange={handleInput} className='form-control rounded-0' name="password" placeholder='Enter Password'/>
                    {errors.password && <span className='text-danger'>{errors.password}</span>}
                </div>
                <Link to="/Concert">
                <input  type='submit'  className='btn btn-success w-100 ' value="Log in"></input>
                </Link>
                <p>You agree to terms and conditions*</p>
                <Link to='/Signup' className='btn btn-default-border w-100 bg-light text-decoration-none'>Create Account</Link>
            </form>
        </div>


    </div>
  )
}
export default Signup;
