import React,{useState} from 'react'
import { Link,useNavigate } from 'react-router-dom'
import validation from './SignupValidation';
import Axios from 'axios';
// import Login from './Login';
//import Login from './Login';

export default function Login() {
    
    const [email, setemail] = useState("")
    const [password, setpassword] = useState("")
    const [L, setregisterStatus] = useState("")
    const navigate=useNavigate()
    const [values,setValues]=useState({
        
        email:"",
        password:""
    })
    const [errors,setErrors]=useState({

    })
    const handleSubmit=(e)=>{
        e.preventDefault();
        Axios.post("http://localhost:8081/Login",{
            
            email:email,
            password:password,
        }).then((res)=>{
            if(res.data.message){
                set(res.data.message);

            }
            else{
                setregisterStatus("Account created")
            }
           
        })
        
    }
    
    const handleInput=(event)=>{
        setValues(prev=>({...prev,[event.target.name]:event.target.value}));
    }

    
  return (
    <div>
 <div className='d-flex justify-content-center align-items-center bg-primary vh-100 '>
        <div className='bg-white p-3 rounded w-25'>
            <form className="mb-3" >
            
                <div>
                    <label htmlFor='email'><strong>Email</strong></label>
                    <input type='email' onChange={(e)=>{setemail(e.target.email)}}  className='form-control rounded-0' id="email" name="email" placeholder='Enter Email' />
                    {errors.email && <span className='text-danger'>{errors.email}</span>}

                </div>
                <div>
                    <label htmlFor='password'><strong>Password</strong></label>
                    <input type='password' onChange={(e)=>{setpassword(e.target.password)}}   className='form-control rounded-0' id="password" name="password" placeholder='Enter Password'/>
                    {errors.password && <span className='text-danger'>{errors.password}</span>}
                </div>
                <button type="submit" onClick={handleSubmit} className='btn btn-success w-100' >Sign up</button>
                <p>You agree to terms and conditions*</p>
                <Link to='/Login' className='btn btn-default-border w-100 bg-light text-decoration-none'>Sign in</Link>
            </form>
        </div>


    </div>

    </div>
  )
}
