import React,{useState} from 'react'
import { Link,useNavigate } from 'react-router-dom'
import validation from './SignupValidation';
import axios from 'axios';
import Login from './Login';
//import Login from './Login';

export default function Signup() {
    const [name, setname] = useState("")
    const [email, setemail] = useState("")
    const [password, setpassword] = useState("")
    const navigate=useNavigate()
    const [values,setValues]=useState({
        name:"",
        email:"",
        password:""
    })
    const [errors,setErrors]=useState({

    })
    const handleSubmit=(event)=>{
        event.preventDefault();
        console.log('Form values:', values);
        setErrors(validation(values));
        if(errors.name===""&& errors.email===""&& errors.password===""){
            axios.post("http://localhost:8081/Signup",values)
            .then(res=>{
                navigate('/Login')
            })
            .catch(err=>console.log(err))
            
        }
    }
    
    const handleInput=(event)=>{
        setValues(prev=>({...prev,[event.target.name]:event.target.value}));
    }

    const register=(e)
  return (
    <div>
 <div className='d-flex justify-content-center align-items-center bg-primary vh-100 '>
        <div className='bg-white p-3 rounded w-25'>
            <form className="mb-3" onSubmit={handleSubmit}>
            <div>
                    <label htmlFor='text'><strong>Name</strong></label>
                    <input type='text' onChange={(e)=>{setname(e.target.name)}} className='form-control rounded-0' id="name" name="name" placeholder='Enter Name' onChange={handleInput}/>
                    {errors.name && <span className='text-danger'>{errors.name}</span>}

                </div>
                <div>
                    <label htmlFor='email'><strong>Email</strong></label>
                    <input type='email' onChange={(e)=>{setemail(e.target.email)}}  className='form-control rounded-0' id="email" name="email" placeholder='Enter Email' onChange={handleInput}/>
                    {errors.email && <span className='text-danger'>{errors.email}</span>}

                </div>
                <div>
                    <label htmlFor='password'><strong>Password</strong></label>
                    <input type='password' onChange={(e)=>{setpassword(e.target.password)}}  onChange={handleInput} className='form-control rounded-0' id="password" name="password" placeholder='Enter Password'/>
                    {errors.password && <span className='text-danger'>{errors.password}</span>}
                </div>
                <button type="submit" onClick={register} className='btn btn-success w-100' >Sign up</button>
                <p>You agree to terms and conditions*</p>
                <Link to='/Login' className='btn btn-default-border w-100 bg-light text-decoration-none'>Sign in</Link>
            </form>
        </div>


    </div>

    </div>
  )
}
