import React,{useState} from 'react'
// import { Link } from 'react-router-dom'
import { Link, useNavigate } from 'react-router-dom'
export default function Concert() {
  const [RegisterStatus, setRegisterStatus] = useState("");
  const navigate = useNavigate();
  const [values, setValues] = useState({
    cname: '',
    cimage: '',
    tname: '',
    timage: '',
    date: '',
    time: '',
    ticket: '',
  });
  const [errors, setErrors] = useState({});
  // const [concertData, setConcertData] = useState([]); // New state to store the retrieved data

  const handleSubmit2 = (e) => {
    e.preventDefault();

    fetch("http://localhost:8081/Edit", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        cname: values.cname,
        cimage: values.cimage,
        tname: values.tname,
        timage: values.timage,
        data: values.date,
        time: values.time,
        ticket: values.ticket
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message) {
          setRegisterStatus(data.message);
          navigate('/Edit2');
        } else {
          setRegisterStatus("Concert created");
          console.log(data);
          navigate('/Edit2');
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const handleInput = (event) => {
    setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
  };
  

  const handleSubmit = (e) => {
    e.preventDefault();

   navigate('/ShowAndId');
  };


  return (
    <>
    <div className=' text-center'>
        <div class="btn-group" role="group" aria-label="Basic example">
  <button type="button" class="btn btn-success" > <Link className="nav-link " aria-current="page" to="/Create">Create New Concert</Link></button>
  <button type="button" class="btn btn-warning"><Link className="nav-link " aria-current="page" to="/Edit">Edit Concert</Link></button>
  <button type="button" class="btn btn-danger"><Link className="nav-link " aria-current="page" to="/Delete">Delete Concert</Link></button>
</div>

<div><button type="submit" onClick={handleSubmit} className="btn btn-success my-3">Show Concerts</button>
</div>

<h1>Edit Concert</h1>
<div className='text-center'>
<div className="mb-3">

  <label htmlFor="exampleFormControlInput1" className="form-label mb-3 my-3 mx-3 ">Concert Id</label>
  <input type="number" onChange={handleInput} className="" id="Ticket_Fee " placeholder="Concert ID to Edit"/>
</div>

<div><button type="submit" onClick={handleSubmit2} className="btn btn-success">Edit</button>
</div>
</div>



    </div>
    </>
  )
}
