import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
let a=0;
let b=0;
export default function ShowAndId() {
    const [concertData, setConcertData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndId');
        const data = await response.json();
        if (Array.isArray(data)) {
          setConcertData(data);
        } else {
          setConcertData([]);
        }
      } catch (error) {
        console.error('Error:', error);
        setConcertData([]); // If there's an error, set concertData to an empty array
      }
    };

    fetchData();
  }, []);
  const [RegisterStatus, setRegisterStatus] = useState("");
  const navigate = useNavigate();
  const [values, setValues] = useState({
    cname: '',
    cimage: '',
    tname: '',
    timage: '',
    date: '',
    time: '',
    ticket: '',
  });
  const [errors, setErrors] = useState({});
  // const [concertData, setConcertData] = useState([]); // New state to store the retrieved data

  const handleSubmit = (e) => {
    a=document.getElementById('id');
    b=a.value;
    console.log(b);
    e.preventDefault();

    fetch("http://localhost:8081/Edit2", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        cname: values.id,
       
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message) {
          setRegisterStatus(data.message);
          navigate('/Edit2');
        } else {
          setRegisterStatus("Concert created");
          console.log(data);
          navigate('/Edit2');
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  const handleInput = (event) => {
    

    setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));

  };
  return (
    <>
    <div className=' text-center'>
        <div class="btn-group" role="group" aria-label="Basic example">
  <button type="button" className="btn btn-success" > <Link className="nav-link " aria-current="page" to="/Create">Create New Concert</Link></button>
  <button type="button" className="btn btn-warning"><Link className="nav-link " aria-current="page" to="/Edit">Edit Concert</Link></button>
  <button type="button" class="btn btn-danger"><Link className="nav-link " aria-current="page" to="/Delete">Delete Concert</Link></button>
</div>

<div>
  <table className="table">
    <thead>
      <tr>
        <th>ID</th>
        <th>Concert Name</th>
        <th>Team Name</th>
        <th>Date</th>
        <th>Time</th>
        <th>Ticket Fee</th>
      </tr>
    </thead>
    <tbody>
      {concertData.map((concert, index) => (
        <tr key={index}>
          <td>{concert.id}</td>
          <td>{concert.cname}</td>
          <td>{concert.tname}</td>
          <td>{concert.date}</td>
          <td>{concert.time}</td>
          <td>{concert.ticket}</td>
        </tr>
      ))}
    </tbody>
  </table>
</div>

<h1>Edit Concert</h1>
<div className='text-center'>
<div className="mb-3">

  <label htmlFor="exampleFormControlInput1" className="form-label mb-3 my-3 mx-3 ">Concert Id</label>
  <input type="number" onChange={handleInput} className="" id="id" placeholder="Concert ID to Edit"/>
</div>

<div><button type="submit" onClick={handleSubmit} className="btn btn-success">Edit</button>
</div>
</div>



    </div>
    </>
  )
}
